CREATE FUNCTION public.prevent_update_finalized_lote() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
  -- Impedir modificaÃ§Ã£o de lotes em estados terminais
  IF OLD.status IN ('finalizado', 'cancelado') THEN
    RAISE EXCEPTION 'Lote com status "%" nÃ£o pode ser modificado', OLD.status;
  END IF;

  -- Se jÃ¡ existe um laudo com status 'enviado', bloquear alteraÃ§Ãµes EXCETO quando
  -- a atualizaÃ§Ã£o tiver como objetivo registrar o envio (laudo_enviado_em) pela
  -- primeira vez. Isto permite que o processo de envio atualize o lote com
  -- timestamps de envio/finalizaÃ§Ã£o sem ser impedido pelo trigger.
  IF EXISTS (
    SELECT 1 FROM laudos WHERE lote_id = OLD.id AND status = 'enviado'
  ) THEN
    -- Permitir apenas a atualizaÃ§Ã£o que define pela PRIMEIRA vez laudo_enviado_em
    IF NOT (NEW.laudo_enviado_em IS NOT NULL AND OLD.laudo_enviado_em IS NULL) THEN
      RAISE EXCEPTION 'Lote possui laudo enviado. ModificaÃ§Ãµes bloqueadas.';
    END IF;
  END IF;

  RETURN NEW;
END;
$$;


